import timeout_decorator

from sanic.headers import * # noqa: autoimport
from sanic.headers import parse_host # noqa: autoimport

LOCAL_TIMEOUT = 5

@timeout_decorator.timeout(LOCAL_TIMEOUT)
def test_0():
    assert parse_host('127.0.0.1:8000')[0] == '127.0.0.1'

@timeout_decorator.timeout(LOCAL_TIMEOUT)
def test_1():
    assert ("192.168.0.1", 42) == parse_host("192.168.0.1:42")

@timeout_decorator.timeout(LOCAL_TIMEOUT)
def test_2():
    assert parse_host("www.python.org") == ("www.python.org", None)

@timeout_decorator.timeout(LOCAL_TIMEOUT)
def test_3():
