import re
from typing import Any, Dict, Iterable, List, Optional, Tuple, Union
from urllib.parse import unquote
from sanic.helpers import STATUS_CODES
import sys
sys.path.insert(1, "/input/test-apps/sanic")
import unittest, pytest
import math
import random
import re
import copy
import datetime
import itertools
import collections
import heapq
import statistics
import functools
import hashlib
import numpy
import numpy as np
import string
from typing import *
from collections import *
import pickle
import timeout_decorator

from sanic.headers import * # noqa: autoimport
from sanic.headers import format_http1_response # noqa: autoimport

LOCAL_TIMEOUT = 5

@timeout_decorator.timeout(LOCAL_TIMEOUT)
def test_1():
    assert (format_http1_response(200, [(b"Server", b"Sanic")])
            == 
            b"HTTP/1.1 200 OK\r\nServer: Sanic\r\n\r\n")

@timeout_decorator.timeout(LOCAL_TIMEOUT)
def test_2():
    assert format_http1_response(200,
            [
                (b'Content-type', b'text/html'),
                (b'Content-Length', b'13')
            ]
        ) == b'HTTP/1.1 200 OK\r\nContent-type: text/html\r\nContent-Length: 13\r\n\r\n'

@timeout_decorator.timeout(LOCAL_TIMEOUT)
def test_3():
    assert format_http1_response(200, [
        (b'Content-Type', b'text/plain'),
        (b'X-Header', b'value'),
        (b'X-Header-Multi', b'value1'),
        (b'X-Header-Multi', b'value2'),
        (b'Set-Cookie', b'key=value'),
        (b'Set-Cookie', b'key2=value2'),
    ]) == b'HTTP/1.1 200 OK\r\nContent-Type: text/plain\r\nX-Header: value\r\nX-Header-Multi: value1\r\nX-Header-Multi: value2\r\nSet-Cookie: key=value\r\nSet-Cookie: key2=value2\r\n\r\n'

@timeout_decorator.timeout(LOCAL_TIMEOUT)
def test_4():
    assert format_http1_response(200, (
        (b'Server', b'Sanic'),
        (b'Connection', b'keep-alive'),
        (b'Keep-Alive', b'timeout=5')
    )) == b'HTTP/1.1 200 OK\r\nServer: Sanic\r\nConnection: keep-alive\r\nKeep-Alive: timeout=5\r\n\r\n'

@timeout_decorator.timeout(LOCAL_TIMEOUT)
def test_5():
    assert b"HTTP/1.1 200 OK\r\nServer: sanic\r\nTransfer-Encoding: chunked\r\n\r\n" == format_http1_response(200, [
        (b"Server", b"sanic"),
        (b"Transfer-Encoding", b"chunked"),
    ])

@timeout_decorator.timeout(LOCAL_TIMEOUT)
def test_6():
    assert format_http1_response(200, [(b"x", b"y"), (b"a", b"b")]) == b'HTTP/1.1 200 OK\r\nx: y\r\na: b\r\n\r\n'

@timeout_decorator.timeout(LOCAL_TIMEOUT)
def test_7():
    assert format_http1_response(500, [(b"a", b"123")]) == b'HTTP/1.1 500 Internal Server Error\r\n' \
